<script lang="ts">
import { Options, Vue } from 'vue-class-component'

@Options({
  mounted () {
    const options = {
      threshold: 0.7,
      root: document.querySelector('#app')
    }
    const buttons = document.querySelectorAll('[data-section-ref]')

    const observer = new IntersectionObserver((entries, observer: IntersectionObserver) => {
      entries.forEach(entry => {
        buttons.forEach(button => {
          if (entry.isIntersecting && button.getAttribute('data-section-ref') === entry.target.id) button.classList.add('active')
          else button.classList.remove('active')
        })
      })
    }, options)

    const sections = document.querySelectorAll('.home__section')
    sections.forEach(section => observer.observe(section))
  },
  methods: {
    scrollToSection (section: string) {
      const elem = document.querySelector(`#${section}`)
      if (elem === null) return
      elem.scrollIntoView({ behavior: 'smooth' })
    }
  }
})
export default class HomeView extends Vue {}
</script>

